mapper.select:
select
  id, InstitutionNo, managerTypeNo, password, name
from accountpasswordtable

    
        and id = #{id}
    
    
        and InstitutionNo = #{institutionno}
    
    
        and managerTypeNo = #{managertypeno}
    
    
        and password = #{password}
    
    
        and name = #{name}
    

limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from accountpasswordtable

    
        and id = #{id}
    
    
        and InstitutionNo = #{institutionno}
    
    
        and managerTypeNo = #{managertypeno}
    
    
        and password = #{password}
    
    
        and name = #{name}
